definition.script:
pipeline {
    agent any
    stages {
        stage('Checkout') {
            steps {
                // 1. 깃허브에서 코드 가져오기 (실제론 Git 설정이 필요하지만, 지금은 로컬 테스트용)
                git branch: 'main', url: 'https://github.com/Mseunghwan/NoSmoke.git'
                // 나중에는 여기에 git url: 'https://github.com/...' 이 들어갑니다.
            }
        }
        stage('Test Backend (Spring)') {
            steps {
                dir('backend') {
                    sh 'chmod +x gradlew'
                    sh './gradlew test'  // 이제 그냥 이렇게만 해도 됨!
                }
            }
        }
        stage('Build Backend') {
            steps {
                dir('backend') {
                    echo 'Building JAR...'
                    sh './gradlew build -x test' // 테스트는 위에서 했으니 생략
                }
            }
        }

        stage('Test AI Server') {
            steps {
                dir('ai-server') {
                    echo 'Testing FastAPI...'
                    // 파이썬 테스트 코드가 있다면 여기서 실행
                    // sh 'python -m pytest' 
                }
            }
        }
    }
}
