definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout') {
            steps {
                // 깃허브에서 코드 가져오기
                git branch: 'main', url: 'https://github.com/Mseunghwan/NoSmoke.git'
            }
        }

        stage('Test Backend (Spring)') {
            steps {
                dir('backend') {
                    // 권한 부여 후 테스트 실행
                    sh 'chmod +x gradlew'
                    sh './gradlew test' 
                }
            }
        }

        stage('Build Backend') {
            steps {
                dir('backend') {
                    echo 'Building JAR...'
                    // 테스트는 위에서 했으니 생략하고 빌드
                    sh './gradlew build -x test' 
                }
            }
        }

        stage('Test AI Server') {
            steps {
                dir('ai-server') {
                    echo 'Testing FastAPI...'
                    // 파이썬 테스트 코드가 있다면 나중에 추가
                }
            }
        }
    }
}
